const formatMoney = (amount = 0) => {
  const options = {
    style: 'currency',
    currency: 'BDT',
    minimumFractionDigits: 2,
  };
  const formatter = Intl.NumberFormat('bn-BD', options);
  return formatter.format(amount / 100);
};

export default formatMoney;
