import React from 'react';
import { TransitionGroup, CsssTransition } from 'react-transition-group';
import styled from 'styled-components';

const Dot = styled.div`
  background: var(--red);
  color: white;
  border-radius: 50%;
  padding: 0.5rem;
  line-height: 2rem;
  min-width: 3rem;
  margin-left: 1rem;
  font-feature-settings: 'tnum';
  font-variant-numeric: tabular-nums;
`;

const CartCount = ({ count }) => (
  <TransitionGroup>
    <CsssTransition
      classNmae="count"
      key={count}
      timeout={{ enter: 400, exit: 400 }}
    >
      <Dot>{count}</Dot>
    </CsssTransition>
  </TransitionGroup>
);

export default CartCount;
