import React from 'react';
import useForm from '../lib/useForm';

const CreateProduct = () => {
  const { inputs, handleChange } = useForm({
    name: 'Nice Shoes',
    price: 34224,
    description: 'These are the best shoes!!!',
  });
  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          id="name"
          name="name"
          placeholder="name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
    </form>
  );
};

export default CreateProduct;
